package Scaler.Assignment27022023;

public class AmazingSubArrays {
    public static void main(String[] args) {
        String str="...";
        char[] chArray=str.toCharArray();
        long totalSubStrings=0;
        for(int i=0;i<=chArray.length-1;i++){
            if(chArray[i]=='a' || chArray[i]=='A'||
                    chArray[i]=='e' || chArray[i]=='E'||
                    chArray[i]=='i' || chArray[i]=='I'||
                    chArray[i]=='o' || chArray[i]=='O'||
                    chArray[i]=='u' || chArray[i]=='U'){
                int subLength=chArray.length-i;
                totalSubStrings=totalSubStrings+subLength;
            }
        }
        System.out.println(totalSubStrings%10003);
    }
}
